import pandas as pdpd.set_option('display.max_rows',None)pd.set_option('display.max_columns',None)pd.set_option('display.width',None)# Define the input and output file pathsinput_file_path = 'all.syn.txt'  # Replace with your input file pathoutput_file_path = 'all.syn.csv'  # Desired output file path# Read the data, skipping the first two comment rowsdf = pd.read_csv(input_file_path, delim_whitespace=True, comment='%', header=None, low_memory=False)# Define column names based on the expected structuredf.columns = [    'Asteroid_ID', 'H', 'a', 'e', 'sin_i', 'n_deg_per_yr',    'g_arcsec_per_yr', 's_arcsec_per_yr', 'LCEx1E6', 'My']# Save the DataFrame to a CSV filedf.to_csv(output_file_path, index=False)print(f"Data has been successfully saved to {output_file_path}.")